/** @jsx jsx */
import { css, jsx } from "@emotion/core";
import styled from "@emotion/styled";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

export default function ChatBar(props) {
  const { sendMessage, setMessageText, messageText } = props;
  const handleMessageSend = () => {
    sendMessage(messageText);
    setMessageText("");
  };
  const handleInput = e => setMessageText(e.target.value);
  const handleKeyPress = e => {
    if (e.key === "Enter") {
      handleMessageSend();
    }
  };
  return (
    <ChatBarLayout>
      <div className="dropdown">
      <FontAwesomeIcon
        icon="ellipsis-v"
        // class="dropbtn"
        css={css`
          cursor: pointer;
          margin-right: 8px;
          margin-top: 6px;
        `}
        onClick={() => {
          document.getElementById("myDropdown").classList.toggle("show");
          // console.log("sup")
          // var dropdowns = document.getElementsByClassName("dropdown-content");
          // console.log(dropdowns)
          // var i;
          // for (i = 0; i < dropdowns.length; i++) {
          //   var openDropdown = dropdowns[i];
          //   if (openDropdown.classList.contains('show')) {
          //     openDropdown.classList.remove('show');
          //   }
          // }
        }}
      />
      <div id="myDropdown" className="dropdown-content">
        <button id="l1" href="#">set avatar
        <input type="file" id="logo" onChange={() => {
          var reader = new FileReader();
          reader.onload = function() {

            var arrayBuffer = this.result,
              array = new Uint8Array(arrayBuffer),
              binaryString = String.fromCharCode.apply(null, array);

            console.log(binaryString);

          }
          reader.readAsArrayBuffer(document.querySelector('input').files[0]);
        }}/>
        </button>
      </div>
      </div>
      <ChatBarInput
        type="text"
        value={messageText}
        onChange={handleInput}
        onKeyDown={handleKeyPress}
      />
      <ChatBarButton onClick={handleMessageSend} />
    </ChatBarLayout>
  );
}
