/** @jsx jsx */
import React, { useRef, useState, useEffect } from "react";
import { css, jsx } from "@emotion/core";
import styled from "@emotion/styled";

import ChatFeed from "./ChatFeed";
import ChatBar from "./ChatBar";
import { usersObserver, channelSubscriptionObserver, channelMessageObserver, ChannelMessage } from "../models";
import { channelSubscriptionsCollection, client, channelMessagesCollection } from "..";
import { ObjectId } from "bson";
import { BSON } from "mongodb-stitch-core-sdk";

const USER = {
  id: "20391029481029849124",
  profile: {
    firstName: "Jessica",
    lastName: "Real",
  },
};

const MESSAGES = [
  {
    ts: 1557355231922,
    from: { id: "40391024481629829197", name: "Jack Example" },
    text: "Wanna meet up with Tim at 8 for dinner?",
  },
  {
    ts: 1557355239361,
    from: { id: "20391029481029849124", name: "Jessica Real" },
    text: "Yeah that works for me!",
  },
  {
    ts: 1557355245967,
    from: { id: "20391029481029849124", name: "Jessica Real" },
    text:
      "What restaurant should we go to? There's a place in the LES that I've wanted to try for a while.",
  },
  {
    ts: 1557355249520,
    from: { id: "40391024481629829197", name: "Jack Example" },
    text: "What kind of food?",
  },
  {
    ts: 1557355255359,
    from: { id: "20391029481029849124", name: "Jessica Real" },
    text: "Mediterranean! Merguez ðŸ˜ðŸ˜ðŸ˜",
  },
  {
    ts: 1557355259651,
    from: { id: "40391024481629829197", name: "Jack Example" },
    text: "Sounds great lets do it!",
  },
];

/* This component manages the state of the entire chat room.
 * NOTE: Requires a logged in Stitch user
 */
export default class ChatRoom extends React.Component {
  async configureCollections() {
    channelMessageObserver.subscribe(event => {
      console.log(event);
      this.state.messages.push(event.fullDocument);
      this.setState(this.state);
    });
    const subscriptionId = await client.callFunction("subscribeToChannel", [
      client.auth.user.id, 
      new ObjectId().toHexString, 
      "default"
    ]);

    console.log("Sub id: " + subscriptionId);

    channelSubscriptionObserver.sync(subscriptionId);
  }

  async sendMessage(text) {
    const msg = ChannelMessage(new BSON.ObjectId(), client.auth.user.id, "default", text, Date.now(), undefined);
    const val = await channelMessagesCollection.insertOne(msg);
    console.log(val);
    this.state.messages.push(msg);
    this.setState(this.state);
  }

  constructor(props) {
    super(props);
    this.state = {
      isLoading: false,
      messages: [],
      messageText: ""
    }
  }

  componentDidMount() {
    this.configureCollections();
  }

  render() {
    // const [isLoading, setIsLoading] = useState(true);
    // const [messages, setMessages] = useState([]);
    // const [messageText, setMessageText] = useState("");

    // useEffect(() => {
    //   // Set up the collection watch stream
    //   // TODO: Need to know the document structure
    //   this.state.setMessages(MESSAGES);
    //   this.state.setIsLoading(false);
    // }, []);

    return <ChatLayout>
      <ChatFeed messages={this.state.messages} />
      <ChatBar
        sendMessage={this.sendMessage}
        setMessageText={(txt) => {
          this.state.messageText = txt
          this.setState(this.state)
        }}
        messageText={this.state.messageText}
      />
    </ChatLayout>;
  }
}
const ChatLayout = styled.div`
  display: flex;
  flex-direction: column;
  width: 100%;
  height: 100vh;
  max-height: 100vh;
  background-color: white;
`;
